    height: calc(var(--line-height-lg)*var(--font-size-lg) + var(--spacer-2)*1);
}

a {
	color: #464646;
	text-decoration: none;
}

a:hover {
    color: #1d1d1d;
    text-decoration: none;
}

.prices--big {
    font-size: 20px;
    font-weight: 500;
}

.variations__option {
