    background: none !important;
    text-decoration: underline !important;
}

.prices:not(.prices--big) {
    font-size: 20px;
    font-weight: 500;
    padding-bottom: 0px;
}

.product__discount {
     color: blue;
}

.prices--literal small {
    font-size: 70%;
}

.footer {
	background: #f6f5f8;
	border-top: none;
	font-size: 13px;
	color: #666;
	font-family: Roboto,Helvetica Neue,sans-serif;
}

.mt-4, .my-4 {
    margin-top: 50px;
}

@media (max-width: 1200px)
.h1, h1 {
    font-size: 30px;
}

.h1, h1 {
    font-size: 30px;
}

.form-control {
    display: block;
    width: 100%;
    height: calc(1.5em + 0.75rem + 2px);
    padding: 0.375rem 0.75rem;
    font-size: 0.9rem!important;
    font-weight: 480!important;
    line-height: 1.5;
    color: #495057;
    background-color: white!important;
