	text-align: center;
	position: absolute;
	z-index: 9;
	top: unset;
	right: 0px;
	border-radius: 2px;
	transition: border-radius .3s;
	opacity: 1;
}

.product-card__picture img {
    max-width: 88%;
}

.product__sticky {
	position: fixed;
	bottom: 0;
	left: 0;
	z-index: 500;
