	border-top: 1px solid #e1e1e1;
	background-color: #ececec;
	/* opacity: 0.95; */
	box-shadow: 0px 0px 10px 1px #00000042;
	opacity: 0.95;
}

.picture img {
    width: auto;
    max-width: 100%;
    height: auto;
    margin: 0 auto;
}

.h5, h5 {
	font-size: 14px;
	font-weight: bold;
	color: #555;
}

.card {
    border: none;
    background: #f1f1f1;

}

.card-header {
padding: 0.75rem 1.25rem;
margin-bottom: 0;
background-color: #1F4F66;
color: whitesmoke;
border-bottom: none;
}

.close:hover {
    color: #fff;
