    margin-bottom: 20px;
   font-family: Roboto,Helvetica Neue,sans-serif;
}

.menu {
    display: none;
    width: 300px;
    position: fixed;
    top: 0;
    left: 0;
    z-index: 1100;
    height: 100vh;
    overflow-y: auto;
    flex-direction: column;
    justify-content: space-between;
    box-shadow: 0px 2px 4px black;
    background: linear-gradient(120deg, #fdfbfb 0%, #ebedee 100%);
}

.menu__item {
    display: flex;
    align-items: center;
    padding: var(--spacer-3);
    border-bottom: var(--border-width) solid #aeb0b3;
    transition: background-color .15s;
    color: #373737;
    font-weight: 501;
}

.menu__item:hover {
    color: black;
    background: rgb(255 255 255 / 65%);
    backdrop-filter: saturate(180%) blur(20px);
}

.menu__footer a {
    color: black;
    text-decoration: none;
    font-size: 13px;
}

.menu__footer a: hover {
    color: black;
    text-decoration: none;
    font-size: 13px;
}

.menu__link {
    text-align: center;
    display: block;
    border-top: var(--border-width) dashed var(--primary-lightest-yiq);
    margin: var(--spacer-3);
    padding: var(--spacer-3) var(--spacer-2);
    line-height: 1;
    color: #000000;
}



.menu__link:hover {
    text-decoration: none;
    color: #000000;
}

.gallery__thumbs {
    z-index: 2;
