css:
.top-bar__countdown {
    font-size: 15px;
    padding: var(--spacer-2) var(--spacer-3);
}

/* cabeçalho */    
.header {
    position: relative;
    padding-bottom: 15px;
    border-bottom: none;
    box-shadow: 0px 2px 4px #000000a1;
    font-family: "Roboto",sans-serif;
    background: #1F4F66;
}



/* barra de informações */ 
.top-bar {
    background: #E9E9E9;
    font-size: 15px;
  font-family: "Roboto",sans-serif;
}
.top-bar__page-links a {
    font-weight: 400;
}
.top-bar__nav {
  color: #3a3a3a;
  background-color: #e9e9e9;
   font-size: 15px;
}

.top-bar__nav {
    padding-left: var(--spacer-3);
    padding-right: var(--spacer-3);
    padding-top: var(--spacer-2);
    padding-bottom: var(--spacer-2);
}

/* botões menu lateral, login e sacola */    
.header__buttons .btn {
color: white;
}
.header__buttons .btn:hover {
  color: white;
 background: #0F5663;
}
.badge-primary {
    color: #1f4f66;
    background-color: #ffffff;
    border-radius: 30%;
}
.header__toggler-icon {
  color: white;
}
.header__toggler-icon:hover {
  color: white;
  background: #0F5663;
}

/* Logo */  
img, svg {
    vertical-align: baseline;
}

 /* texto do menu */    
.header__nav a {
text-transform: uppercase;
color: white;
font-size: 13px;
}
.header__nav a:hover {
background-color: #E6E6E6;
color: black;
}

.header__nav--full a {
    margin-top: var(--spacer-2);
    padding: 0 var(--spacer-3);
}

/* barra de pesquisa */  
.header__search-input {
    padding: 0 var(--spacer-3);
    border-radius: 17px;
    font-size: 14px;
    line-height: var(--line-height-lg);
    height: calc(var(--line-height-lg)*var(--font-size-lg) + var(--spacer-2)*1);
}

a {
    color: #1f4f66;
    text-decoration: none;
}

a:hover {
  text-decoration: underline;
  color: #1f4f66;
}

