/*Preços pequenos*/
.prices--big {
    font-size: 20px;
    font-weight: 500;
}


/* Variantes da página do produto */
.variations__option {
    min-height: 2.3rem;
    min-width: 2.3rem;
    margin-bottom: var(--spacer-2);
    border: none !important;
}

.btn-light {
    color: #000000;
    background-color: #f3f3f3;
    border-color: #ffffff;
    border-radius: 7px;
}

.btn-light:hover {
    color: #000000;
    background-color: #f3f3f3;
    border-color: black;
    border-radius: 7px;
}

/* Botão de compra */
.product__buy button {
text-transform: none;
transition: 0.5s;
background: linear-gradient(
180deg,#fc5d37 0%,#ed5132 49%,#e64525 100%);
background-size: 200% auto;
color: white;            
box-shadow: none;
border-radius: 4px;
display: block;
width: 100%;
border: none;
}

.product__buy button:hover {
background-color: linear-gradient(
180deg,#fc5d37 0%,#ed5132 49%,#e64525 100%);
color: white;
text-decoration: none;
}

.btn-primary {
    color: #f3f3f3;
background: linear-gradient(
180deg,#fc5d37 0%,#ed5132 49%,#e64525 100%);
    border-color: gray;
    border-radius: 4px 4px 4px 4px;
}

.btn-primary:hover {
    color: #f3f3f3;
background-color: linear-gradient(
180deg,#fc5d37 0%,#ed5132 49%,#e64525 100%);
    border-color: gray;
}

.btn-outline-secondary {
    color: #f3f3f3;
    background-color: #1F4F66;
    border-color: gray;
    border-radius: 4px 4px 4px 4px;
}

.btn-outline-secondary:hover {
    color: #f3f3f3;
    background-color: #1F4F66;
    border-color: gray;
}

/* Preços do container */
.prices:not(.prices--big) {
    font-size: 20px;
    font-weight: 500;
    padding-bottom: 15px;
}

/* Aleatório */
.mt-3, .my-3 {
    margin-top: 1rem;
    font-size: 14px;
    color: #ba0c2f;
}

.product__discount {
    color: #810000;
}

.lead {
    font-weight: 400;
}
.prices--literal small {
    font-size: 70%;
}

.footer {
    background: #f1f1f1;
    border-top: var(--spacer-1) solid #4f4f4f00;
  font-size: 13px;
    color: #666;
}

.mt-4, .my-4 {
    margin-top: 50px;
}

@media (max-width: 1200px)
.h1, h1 {
    font-size: 30px;
}

.h1, h1 {
    font-size: 30px;
}

img, svg {
    vertical-align: top;
}

.form-control {
    display: block;
    width: 100%;
    height: calc(1.5em + 0.75rem + 2px);
    padding: 0.375rem 0.75rem;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: #495057;
    background-color: #fff;
    background-clip: padding-box;
    border: 1.5px solid #bbbbbb;
    border-radius: 0.25rem;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
}

.product-card__offer-stamp {
    background-color: #d10014;
    color: #ffffff;
    min-width: 55px;
    font-size: 80%;
    font-weight: 400;
    line-height: 2.2;
    text-align: center;
    position: absolute;
    z-index: 9;
    top: unset;
    right: 0;
    border-radius: var(--border-radius);
    transition: border-radius .3s;
    opacity: 1;
}

.product-card__picture img {
    max-width: 88%;
}

.product__sticky {
    position: fixed;
    bottom: 0;
    left: 0;
    z-index: 500;
    width: 100%;
    padding: var(--spacer-2) var(--spacer-3);
    background: rgb(255 255 255 / 65%);
    box-shadow: var(--box-shadow-lg);
    backdrop-filter: saturate(180%) blur(20px);
}

.picture img {
    width: auto;
    max-width: 100%;
    height: auto;
    margin: 0 auto;
}

.h5, h5 {
    font-size: 15px;
}

.card {
    border: none;
}

.card-header {
    padding: 0.75rem 1.25rem;
    margin-bottom: 0;
    background-color: #1F4F66;
    color: whitesmoke;
  border-bottom: none;
}

.close:hover {
    color: #fff;
    text-decoration: none;
}

.close {
    float: right;
    font-size: 1.5rem;
    font-weight: 700;
    line-height: 1;
    color: whitesmoke;
    text-shadow: none;
    opacity: 1;
}

.cart-item .close {
    padding-left: var(--spacer-2);
    color: #525252 !important;
    font-size: var(--font-size);
}

.card-footer {
    padding: 0.45rem 1.25rem;
    background-color: rgb(0 0 0 / 7%);
    border-top: none;
}

.search__header {
    position: relative;
    display: flex;
    align-items: center;
    background: #ededed;
}

.form-control {
    display: block;
    width: 100%;
    height: calc(1.5em + 0.75rem + 2px);
    padding: 0.375rem 0.75rem;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: #495057;
    background-color: #fff;
    background-clip: padding-box;
    border: 1.5px solid #ffffff;
    border-radius: 0.25rem;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
}

.prices--big .prices__value {
    font-size: var(--h3);
    margin-bottom: var(--spacer-1);
}

.table {
    width: 100%;
    margin-bottom: 1rem;
    color: #212529;
    box-shadow: 0 2px 4px 2px rgb(0 0 0 / 16%);
}

.table td, .table th {
    padding: 0.75rem;
    vertical-align: top;
    border-top: none;
}

.product__sticky-buy .btn-primary{
background-color: #25BA34;
border-color: #25BA34;
}

.product__sticky-buy .btn-primary:hover{
background-color: #1B8726;
border-color: #1B8726;
}

.mt-3, .my-3 {
    margin-top: 1rem;
    font-size: 14px;
    color: #404040;
}

@media (min-width: 576px)
.header__logo {
    max-width: 180px !important;
    max-height: 60px;
}

img, svg {
    vertical-align: unset;
}

.prices {
    line-height: unset;
}

.product__payment>a {
    display: flex;
    justify-content: space-between;
    align-items: center;
    background: #f7f7f7;
    color: #2e2e2e;
    margin-top: 15px;
}

.input-group:not(.has-validation)>.form-control:not(:last-child) {
    border-top-right-radius: 0;
    border-bottom-right-radius: 0;
    border-color: #e9e9e9;
}

.info-bar {
    width: 100%;
    max-width: 80rem;
    margin-left: auto;
    margin-right: auto;
    background: #333333;
    color: white;
}

.menu {
    display: none;
    background: #1F4F66;
    width: 300px;
    position: fixed;
    top: 0;
    left: 0;
    z-index: 1100;
    height: 100vh;
    overflow-y: auto;
    flex-direction: column;
    justify-content: space-between;
  box-shadow: 0px 2px 4px black;
}

.menu__item {
    display: flex;
    align-items: center;
    color: var(--primary);
    padding: var(--spacer-3);
    border-bottom: var(--border-width) solid #aeb0b3;
    transition: background-color .15s;
}

.menu__footer a {
    color: #dddddd;
    text-decoration: none;
}

.menu__footer a: hover {
    color: white;
    text-decoration: none;
}
