    font-weight: 600;
    color: #454545;
}

.prices__installments {
    font-size: 15px;
    font-weight: 400!important;
}

.product-card__offer-stamp i, .product-card__offer-stamp svg {
    font-size: 10px;
    opacity: 1;
}

#product-description .container {
    max-width: 100%;
    padding-left: 0;
    padding-right: 0;
    width: 100%;
}

#description .p.lead {
    color: #404040;
    font-size: 20px;
}

.product__short-stock {
    background: white;
    border-radius: var(--border-radius-sm);
    display: inline-block;
    font-weight: var(--font-light);
    padding: var(--spacer-1) var(--spacer-3);
    text-transform: lowercase;
    color: #5f5f5f;
}

.product__production i, .product__production svg, .product__short-stock i, .product__short-stock svg {
    color: #5f5f5f;
}

.container {
    font-size: 13px;
    font-weight: 300;
      max-width: 95%;
}

#product-description p.lead {
    color: #212529;
    font-weight: 500;
    font-size: 18px;
    margin-left: 10px;
  margin-right: 10px;
  display: none;
}

.shipping-line>span {
    display: inline-block;
    color: green;
}

p.product__production {
    display: none;
}
.custom-select:disabled {
    background-color: #ffffff;
    color: #6c757d;
}

button.minicart__btn-back.btn.btn-block.btn-sm.btn-link.d-md-none {
    color: black;
}

.product__payment.card.mb-3 {
    background: white!important;
}

div#product-payment {
    border: 1px solid rgb(241 241 241);
}

button.btn.btn-outline-secondary {
    box-shadow: none!important;
  background: #f1f1f1;
}

button.btn.btn-outline-secondary:hover {
    background: #f1f1f1;
}
i.i-search:hover {
    color: #999999;
}

.search__history {
    color: var(--text-muted);
    font-weight: var(--font-light);
    overflow-x: auto;
    white-space: nowrap;
    font-size: 13px!important;
}
.menu__list {
    padding-top: 40px;
    font-size: 13px!important;
}

.menu__item {
    display: flex;
    align-items: center;
    color: #2e2e2e;
    padding: var(--spacer-3);
    border-bottom: var(--border-width) solid #d9d9d9;
    transition: background-color .15s;
}

.menu__link {
    text-decoration: none;
    color: #464646;
    font-size: 13px;
}

.menu__link:hover {
    text-decoration: none;
    color: black;
    font-size: 13px;
}
i.i-arrow-left {
    font-size: 13px;
}
i.i-times {
    font-size: 13px;
}

label.custom-control-label {
    color: black;
}
button.btn.text-truncate {
    font-weight: 400;
    font-size: 14px;
    color: #2493A1;
}

.search-engine__filter button i, .search-engine__filter button svg {
    color: var(--gray);
    font-size: 12px;
    margin-right: var(--spacer-1);
    opacity: .5;
    transition: opacity .25s;
}

button.search-engine__aside-open.btn.btn-secondary {
    font-size: 12px;
    background: #f3f3f3;
    border: none;
    color: #4f4f4f;
}

[vw] [vw-access-button] .access-button {
    width: 22%!important;
    float: right!important;
}

span.payment-option__discount.badge.badge-success {
	background: #FFE5ED !important;
	border-radius: 0px;
	color: #DF285F !important;
}

.i-home {
    color: #595959;
}

button.btn.btn-sm.btn-light.mt-3.offers-notification {
    color: #a68100;
    font-size: 13px;
    font-weight: 300;
    background: #ffefd559;
}
button.variations__option.btn.btn-light.variations__option {
	color: #313131;
	border-radius: 0px;
	/* border: var(--border-width) solid var(--border-color) !important; */
}


i.i-forward.mr-1 {
    color: #8a8a8a;
}

.checkout__step {
    color: #838383;
    flex-grow: 1;
    font-size: .85rem;
    padding: .35rem;
    position: relative;
    text-align: center;
    text-transform: uppercase;
}

.shipping-line>small {
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    color: var(--text-muted);
    display: block!important;
    line-height: var(--line-height-sm);
    overflow: visible!important;
}

span.mr-2 {
    color: green;
    font-size: 13px;
}

.btn-success {
    background-color: #25B1C2;
    color: var(--white);
    box-shadow: 1px 1px 2px darkgrey;
      border-color: #0000ff00;
}

.btn-success:hover {
    background-color: #25aabb;
    color: var(--white);
    box-shadow: 1px 1px 2px darkgrey;
      border-color: #0000ff00;
}

a.cart__btn-back.btn.btn-link {
    color: #444444;
    font-size: 14px;
}

.cart-item__name {
    font-size: 13px!important;
}

.shipping-calculator__option>small {
    position: relative;
}

.cart__info {
    background: #eeeeee;
    border-radius: .25rem;
    padding: 1rem;
}

.list-group-item-action {
    color: #495057;
    text-align: inherit;
    width: 100%;
    height: 135px;
}

.cart .cart-item {
    border-top: 1px solid #e1e1e1!important;
    padding-top: 1rem;
}

.list-group-item {
    background-color: #fff;
    border: 1px solid rgba(0,0,0,.125);
    display: block;
    padding: .75rem 1.25rem;
    position: relative;
}

a.btn.btn-sm.product__favorite {
    font-weight: 300;
}

.pswp__item {
    background: white;
}

.pswp__caption__center {
    color: #000!important;
    font-size: 13px;
    line-height: 20px;
    margin: 0 auto;
    max-width: 420px;
    padding: 10px;
    text-align: left;
}

span.product__discount {
	background: #c200d2;
	border-radius: 1px;
	color: #FFF;
	padding-left: 7px;
	padding-right: 7px;
}
.products-carousel {
    background: #f9f9f9;
    margin: var(--spacer-4) auto;
    max-width: var(--content-max-width);
    min-height: 6rem;
    padding: 0 var(--spacer-2);
    border-radius: 0px;
}

.products-carousel__title a, .products-carousel__title span {
    background: #f9f9f9;
    color: inherit;
    display: inline-block;
    padding-left: var(--spacer-3);
    padding-right: var(--spacer-3);
    position: relative;
}

.products-carousel__title {
    display: block;
    margin: 0;
    padding: var(--spacer-3);
    position: relative;
    text-align: left;
    font-size: 20px!important;
}

.custom-content {
    padding-left: 2%;
}

.prices small:not(.prices__discount-label) {
    text-transform: lowercase;
    font-size: 12px;
    color: #717171;
}

s {
    text-decoration: none;
    color: gray;
    font-size: 18px;
}
.category-retail {
    background: #fafafa;
}

.product__out-of-stock, .product__unavailable {
    color: #e3e3e3;
    font-size: var(--font-size-lg);
    font-weight: 500;
    margin-bottom: var(--spacer-3);
}

.badge-warning {
    background-color: #ffc107;
    color: #000000;
}
.col-auto {
	color: #e3e3e3;
}
.col-auto:hover {
	color: white!important;
}

.header__container {
    margin-bottom: var(--spacer-1);
    margin-top: var(--spacer-2);
}

i.i-play {
    color: #ff0000!important;
    margin-right: 8%!important;
}

li.glide__slide.brands-carousel__item {
    padding: 4%;
}
.brand-retail {
    background: #f8f9fa;
}

.btn-light.focus, .btn-light:focus {
}

.gallery__item--video > span {
	display: none!important;
} 

.btn.btn-primary.btn-block {
	background: #25B1C2;
}
.btn.btn-primary.btn-block:hover {
	background: #2CC0D2;
}
.header__toggler-icon.fas.fa-bars {
	color: #E3E3E3;
  background-color: #17A1B7;
}
.header__toggler-icon.fas.fa-bars:hover {
	color: #E3E3E3;
  background-color: #17A1B7!important;
}

.fas.fa-user {
	color: #E3E3E3;
}
.fas.fa-shopping-cart {
	color: #E3E3E3;
}
.fas.fa-shopping-cart.mr-1 {
	color: #F2F2F2!important;
}
#shipping-calculator-zip {
	border: 1px solid #f1f1f1 !important;
}
.cart-item__prices {
	color: #000;
}

.variations__option--selected:not(.disabled) {
	border-color: var(--secondary);
	box-shadow: 0 0 0 .1rem #25b1c2;
   color: #fff !important;
   background: #25b1c2;
}

.btn-light:not(:disabled):not(.disabled).active, .btn-light:not(:disabled):not(.disabled):active, .show > .btn-light.dropdown-toggle {
	/* background-color: #dae0e5; */
	border-color: #d3d9df;
	color: var(--gray-dark);
}
