}

.picture img {
    width: auto;
    max-width: 100%;
    height: auto;
    margin: 0 auto;
}

.card {
    border: none;
    background: #f1f1f1;

}

.card-header {
padding: 0.75rem 1.25rem;
margin-bottom: 0;
background-color: #1F4F66;
color: whitesmoke;
border-bottom: none;
}

.close:hover {
    color: #fff;
    text-decoration: none;
}

.close {
    float: right;
    font-size: 1.5rem;
    font-weight: 700;
    line-height: 1;
    color: whitesmoke;
    text-shadow: none;
    opacity: 1;
}

.cart-item .close {
    padding-left: var(--spacer-2);
    color: #525252 !important;
    font-size: var(--font-size);
}

