}

.table {
    width: 100%;
    margin-bottom: 1rem;
    color: #555555;
}


.product__sticky-buy .btn-primary {
	background-image: linear-gradient(to right, #373737 0%, #2d2d2d 100%) !important;
	/* border-color: #032c7d ; */
	text-decoration: none;
	color: #000;
	box-shadow: none;
  padding-top: 5px !important;
    font-size: 18px !important;
    color: white !important;
    border-radius: 10px !important;
}
.product__sticky-buy .btn-primary:hover {
   background-position: right center !important;
	color: #000;
	box-shadow: none;
}

.prices {
    line-height: unset;
    color: #5d5d5d;
}

.product__payment>a {
