    margin-bottom: 20px;

}

.menu {
    display: none;
    width: 300px;
    position: fixed;
    top: 0;
    left: 0;
    z-index: 1100;
    height: 100vh;
    overflow-y: auto;
    flex-direction: column;
    justify-content: space-between;
    box-shadow: 0px 2px 4px #00000078;
    background: #e8e8e8;
}

.menu__item {
    color: #ffffff;
    border-bottom: 1px solid #adadad21 !important;
}

