    background: #f7f7f7 !important;
}

button.close {
    background-color: transparent;
    border: 0;
    color: #525252;
}
button.close:hover {
    background-color: transparent;
    border: 0;
    color: #525252;
}

.minicart__summary {
    color: #fff;
