css:
@media (min-width: 576px){
.variations__grid.variations__grid--colors button.variations__option.btn.btn-light.variations__option--selected:not(.disabled){
	box-shadow: 0px 0px 3px 3px #00000047 !important;
	border: 1px solid #d2d2d2 !important;
  }
}

#s-macbook {
grid-template-columns: repeat(auto-fit,minmax(50%,1fr)) !important;
}
#s-macmini {
grid-template-columns: repeat(auto-fit,minmax(50%,1fr)) !important;
}
#s-imac {
grid-template-columns: repeat(auto-fit,minmax(50%,1fr)) !important;
}

.variations__grid.variations__grid--colors button.variations__option.btn.btn-light.variations__option--selected:not(.disabled) {
	box-shadow: none !important;
	border: 1px solid #454545 !important;
}
button.discount-applier__coupon-btn.btn.btn-sm.btn-light {
	color: #8d4a86 !important;
	font-size: 13px !important;
	font-weight: 600 !important;
	border-radius: 0px !important;
    border: none !important;
}

.btn.btn-primary {
	background: #f2f2f2 !important;
	box-shadow: none !important;
	border-radius: 0px !important;
}

.btn.btn-primary:hover {
	background: #f2f2f2 !important;
}

.top-bar__countdown {
    background: #f7f7f7 !important;
    color: #454545 !important;
    font-size: 14px;
    padding: var(--spacer-1) var(--spacer-2);
    font-weight: 499;
  display: none;
}

.header {
    position: relative;
    /* padding-bottom: 15px; */
    border-bottom: none;
    background: #2e3690 ;
    /* box-shadow: 0px 4px 2px -3px #9d9d9d; */
    /* border-bottom: 2px solid #FFF; */
    /* background: linear-gradient(-180deg,#2e3690 ,#0b9abb); */
    /* border-bottom: 4px solid #e3e3e3; */
    box-shadow: none !important;
    padding: var(--spacer-1) var(--spacer-3);
}

.top-bar__page-links a {
	font-weight: 600;
	font-size: 11px;
	color: #e3e3e3 !important;
	margin-right: 25px !important;
}

.top-bar__page-links a:hover {
	text-decoration: underline;

}

.top-bar__nav {
	padding-left: var(--spacer-8) !important;
	font-size: 15px;
	background: #2e3690 ;
	/* border-radius: 0px 0px 3px 3px; */
	/* border-bottom: 1px solid #2e3690; */
	color: #e3e3e3 !important;
}

.top-bar__nav {
    padding-left: var(--spacer-3);
    padding-right: var(--spacer-3);
}

.header__buttons .btn {
    color: white;
}

.header__buttons .btn:hover {
    color: #eaeaea;
}

.badge-primary {
	color: #ffffff;
	background-color: #298c39;
	font-weight: bold;
	border-radius: 5px !important;
	border: 2px solid #298c39;
	font-size: 11px !important;
}

.header__toggler-icon:hover {
    color: white;
    background: #2493A1;
}

img, svg {
    vertical-align: top !important;
}

.header__nav a {
	font-size: 14px !important;
	font-weight: 400;
	color: #e3e3e3;
	/* text-shadow: 1px 1px 0px #7d7d7d; */
	/* text-shadow: none; */
	text-transform: none;
}

.header__nav.header__nav--full a:hover {
	text-decoration: underline;
	background: none !important;
  color: #e3e3e3 !important;
}

.header__nav--full a {
    margin-top: var(--spacer-2);
    padding: 0 var(--spacer-3);

}

.header__search-input {
    padding: 0 var(--spacer-3);
    border-radius: 17px;
    font-size: 14px;
    line-height: var(--line-height-lg);
    height: calc(var(--line-height-lg)*var(--font-size-lg) + var(--spacer-2)*1);
}

a {
	color: #464646;
	text-decoration: none;
}

a:hover {
    color: #1d1d1d;
    text-decoration: none;
}

.prices--big {
    font-size: 20px;
    font-weight: 600;
}

.variations__option {
    min-height: 30px !important;
    min-width: 2.3rem;
    margin-bottom: var(--spacer-2);
    font-size: 14px;
}

.product__buy {
	/* width: 254px !important; */
     margin-top: -5px !important;
}

.product__buy button {
	text-transform: none;
	transition: 0.5s;
	background: none;
	background-size: 200% auto;
	color: #FFF;
	border-radius: 3px;
	display: block;
	border: none;
	box-shadow: 1px 1px 2px darkgrey !important;
	font-size: 18px;
	/* padding-bottom: 10px; */
	/* padding-top: 12px; */
}

.product__buy button:hover {
    background: #2e3690 ;
    color: #F2F2F2;
    text-decoration: none;
    box-shadow: 1px 1px 2px darkgrey;
}

.btn-primary {
    color: white;
    background: #216b74 !important;
    border-color: gray;
    border: none;
    border-radius: 4px 4px 4px 4px;
    box-shadow: 1px 1px 2px darkgrey;
}

.btn-primary:hover {
    color: white;
    background: #195a62 !important;
    border: none;
    box-shadow: 1px 1px 2px darkgrey;
}

.minicart__btn-cart.btn.btn-block.btn-outline-secondary {
	color: #424242 !important;
	background: none !important;
	border: none !important;
	border-radius: 4px 4px 4px 4px !important;
	font-size: 13px !important;
	font-weight: 600 !important;
}

.minicart__btn-cart.btn.btn-block.btn-outline-secondary:hover {
    background: none !important;
    text-decoration: underline !important;
}

.prices:not(.prices--big) {
    font-size: 20px;
    font-weight: 600;
    padding-bottom: 0px;
}

.product__discount {
     color: blue;
}

.prices--literal small {
    font-size: 70%;
}

.footer {
	background: #2e3690;
	border-top: none;
	font-size: 13px;
	color: #d3d3d3;
	box-shadow: none !important;
	border-top: 1px solid rgba(0,0,0,.1);
	border-bottom: 1px solid rgba(0,0,0,.1);
}

.mt-4, .my-4 {
    margin-top: 50px;
}

@media (max-width: 1200px)
.h1, h1 {
    font-size: 30px;
}

.h1, h1 {
    font-size: 30px;
}

.form-control {
    display: block;
    width: 100%;
    height: calc(1.5em + 0.75rem + 2px);
    padding: 0.375rem 0.75rem;
    font-size: 0.9rem!important;
    font-weight: 600!important;
    line-height: 1.5;
    color: #454545;
    background-color: white!important;
    background-clip: padding-box;
    border: 1px solid #ced4da !important;
    border-radius: 2px !important;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
    text-overflow: ellipsis!important;
}

.product-card__offer-stamp {
	font-size: 11px;
	font-weight: bold;
	line-height: 2;
	top: unset;
	opacity: 1;
	background: #348c46  !important;
}

.product__sticky {
    background: rgb(0 0 0 / 77%);
    backdrop-filter: saturate(328%) blur(23px);
}

.picture img {
    width: auto;
    max-width: 100%;
    height: auto;
    margin: 0 auto;
}

.card {
    border: none;
    background: #f1f1f1;

}

.card-header {
padding: 0.75rem 1.25rem;
margin-bottom: 0;
background-color: #1F4F66;
color: whitesmoke;
border-bottom: none;
}

.close:hover {
    color: #fff;
    text-decoration: none;
}

.close {
    float: right;
    font-size: 1.5rem;
    font-weight: 700;
    line-height: 1;
    color: whitesmoke;
    text-shadow: none;
    opacity: 1;
}

.cart-item .close {
    padding-left: var(--spacer-2);
    color: #525252 !important;
    font-size: var(--font-size);
}

.card-footer {
    padding: 0.45rem 1.25rem;
    background: #fafafa;
    border-top: none !important;
    box-shadow: 3px 7px 16px #0000003d !important;
}

.search__header {
    position: relative;
    display: flex;
    align-items: center;
    background: #ededed;
}

.form-control {
    display: block;
    width: 100%;
    height: calc(1.5em + 0.75rem + 2px);
    padding: 0.375rem 0.75rem;
    font-size: 13px;
    line-height: 1.5;
    color: #495057;
    background-color: #fff;
    background-clip: padding-box;
    border: 1.5px solid #ffffff;
    border-radius: 0.25rem;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
}

.prices--big .prices__value {
	font-size: 25px;
	margin-bottom: var(--spacer-1);
	color: #454545;
	font-weight: 500 !important;
}

.prices__value {
    display: block;
    font-size: 18px;
    color: black;
    font-weight: 600;
}

.table {
    width: 100%;
    margin-bottom: 1rem;
    color: #2e3690;
}


.product__sticky-buy .btn-primary {
	background-image: linear-gradient(to right, #434343 0%, black 100%)!important; 
	/* border-color: #2e3690 ; */
	text-decoration: none;
	color: #000;
	box-shadow: none;
  padding-top: 5px !important;
    font-size: 18px !important;
    color: white !important;
    border-radius: 50px !important;
}
.product__sticky-buy .btn-primary:hover {
   background-position: right center !important;
	color: #000;
	box-shadow: none;
}

.prices {
    line-height: unset;
    color: #5d5d5d;
}

.product__payment>a {
    display: flex;
    justify-content: space-between;
    align-items: center;
    background: #f7f7f7;
    color: #2e2e2e;
    margin-top: 15px;
    font-size: 14px;
    font-weight: 400;
}

.input-group:not(.has-validation)>.form-control:not(:last-child) {
    border-top-right-radius: 0;
    border-bottom-right-radius: 0;
    border-color: #e9e9e9;
}

.info-bar {
    width: 100%;
    max-width: 80rem;
    margin-left: auto;
    margin-right: auto;
    background: #3f3f3f;
    color: white;    
    margin-top: 20px;
    margin-bottom: 20px;

}

.menu {
    display: none;
    width: 300px;
    position: fixed;
    top: 0;
    left: 0;
    z-index: 1100;
    height: 100vh;
    overflow-y: auto;
    flex-direction: column;
    justify-content: space-between;
    box-shadow: 0px 2px 4px #00000078;
    background: #e8e8e8;
}

.menu__item {
    display: flex;
    align-items: center;
    padding: var(--spacer-3);
    border-bottom: none;
    color: #373737;
    font-weight: 600;
}

.menu__item:hover {
    color: black;
    background: rgb(255 255 255 / 65%);
    backdrop-filter: saturate(180%) blur(20px);
}

.menu__footer a {
    color: black;
    text-decoration: none;
    font-size: 13px;
}

.menu__footer a: hover {
    color: black;
    text-decoration: none;
    font-size: 13px;
}

.menu__link {
    text-align: center;
    display: block;
    border-top: var(--border-width) dashed var(--primary-lightest-yiq);
    margin: var(--spacer-3);
    padding: var(--spacer-3) var(--spacer-2);
    line-height: 1;
    color: #000000;
}



.menu__link:hover {
    text-decoration: none;
    color: #000000;
}

.gallery__thumbs {
    z-index: 2;
    position: absolute;
    width: auto;
    padding: 0;
    display: inline-flex;
    top: 100%;
    margin: 0px 0 0 !imoportant;
}

.product__picture picture.picture img {
    position: absolute;
    top: 0;
    left: 0;
    background: #ffffff;
}

.search__status .close {
    transition: opacity .2s;
    color: #7a7a7a;
}

.product-card__name {
    --max-lines: 3;
    padding-top: var(--spacer-1);
    font-size: 12px;
    line-height: var(--line-height-sm);
    overflow: hidden;
    z-index: 1;
    color: #5f5f5f !important;
    font-weight: bold;
    color: #454545 !important;

}

.card-header:first-child {
	border-radius: 0px;
	background: #2e3690  !important;
	border-bottom: 1px solid #7b7b7b0f;
	font-weight: 600 !important;
	color: #fff !important;
	font-size: 14px !important;
}

.product-quickview__box {
    width: 90vw;
    margin-left: 5vw;
    max-height: 90vh;
    margin-top: 5vh;
    border-radius: var(--border-radius);
    background: white;
}

button.close {
    background-color: transparent;
    border: 0;
    color: #525252;
}
button.close:hover {
    background-color: transparent;
    border: 0;
    color: #525252;
}

.minicart__summary {
    color: #2b2b2b;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding-bottom: var(--spacer-3);
}
.minicart__subtotal {
    text-align: right;
    color: #1c1c1c;
}

@media (min-width: 576px)
.app__header {
    padding: var(--spacer-1);
    text-align: right;
    font-size: var(--font-size);
    color: whitesmoke;
    border-bottom: 5px solid #d3d3d3;
}

.app__header {
    background: #2e3690 ;
    padding: var(--spacer-1);
    text-align: center;
    font-size: var(--font-size-sm);
    border-bottom: none;
    box-shadow: 1px 1px 1px #8080807d;
}

.app__contact span:not(:last-child) {
    margin-right: var(--spacer-3);
    color: whitesmoke;
}

.header__toggler-icon {
    color: #ffffff;
    font-size: 1.3rem;
    margin-right: var(--spacer-1);
    margin-bottom: -0.13rem;
}

.breadcrumb {
    background-color: transparent;
    border-radius: 0.25rem;
    display: flex;
    flex-wrap: wrap;
    list-style: none;
    margin-bottom: -30px;
    padding: 0;
    font-size: 14px;
    font-weight: 400;

}

.header__nav {
    font-size: var(--font-size-sm);
    line-height: 18px;
    text-align: center;
    border-radius: 10px;
}

@media screen and (max-width: 1000px) {
 .d-none {
    display: block !important;
    padding: 0px !important;
}

}
  @media screen and (max-width: 1000px) {
.header__nav--full a {
    margin-top: var(--spacer-2);
    padding: 0 var(--spacer-2);
  }
}

.variations__value {
	font-size: 13px !important;
	margin-left: var(--spacer-2);
	color: gray;
    font-weight: 600;
}

.variations__value--empty {
    font-size: 13px!important;
    margin-left: var(--spacer-2);
    color: #9f9f9f;
}


.lead {
	font-size: 13px;
    font-weight: normal;
}

span.ml-1.d-none.d-xl-inline-block {
    color: #3e4145;
    font-weight: 600;
    font-size: 13px;
}

.app__credits {
    color: var(--gray);
