}  

.fas.fa-user {
    vertical-align: 5px;
    /* content: url('https://api.iconify.design/fluent/person-circle-24-filled.svg?color=%23e3e3e3&width=31'); */
    content: url('https://api.iconify.design/iconamoon/profile-thin.svg?color=%23e3e3e3&width=35');
}


.alert-warning {
	background-color: #fff3cd7a;
