    font-weight: 400 !important;
      border-top: 1px solid #7f7f7f54;
}
.row {
    display: flex;
    flex-wrap: wrap;
    margin-left: -15px;
    margin-right: -15px;
    font-size: 13px;
}
.footer__title[aria-expanded] {
    border: none;
    padding-bottom: 0;
    padding-top: 0;
    text-transform: uppercase;
}
.footer__title {
    text-transform: uppercase;
    font-size: 15px;
}
.footer__links i {
    color: var(--text-muted);
