}
.info-bar__item:hover {
    text-transform: none;
    color: #d1d1d1;
    background: unset;
    font-size: 13px;
}
.info-bar {
    width: 100%;
    max-width: 80rem;
    margin-left: auto;
    margin-right: auto;
    color: black;
}
