    padding-top: 0.875rem;
    text-align: center;
    width: 3.25rem;
    background: none;
    color:white;
}

.list-inline, .list-unstyled {
    list-style: none;
    padding-left: 0;
    font-size: 12px;
}
address {
    font-style: normal;
    line-height: inherit;
    font-size: 12px;
}

.product-card__picture img {
    border-radius: 10px;
}

.table td, .table th {
