    border-top: none;
    font-size: 12px;
}

i.i-chevron-left {
    font-size: 17px!important;
}

i.i-chevron-right{
    font-size: 17px!important;
}

.glide .glide__bullet--active {
    background-color: var(--secondary-lighter);
    border-color: var(--secondary);
    cursor: auto;
    opacity: .8;
    max-width: 25px;
    max-height: 5px;
}

glide__bullet {
    background: var(--secondary-lightest);
    border: 1.5px solid var(--secondary-light);
    border-radius: 0.3rem;
    cursor: pointer;
    height: 0.6rem;
    line-height: 0;
    margin: 0 0 0 0.25rem;
    opacity: .6;
    padding: 0;
    transition: opacity .15s ease,background-color .2s,border .2s;
    width: 1rem;
    z-index: 3;
    max-width: 25px!important;
    max-height: 5px!important;
}

.info-bar__item:after {
    background: #494949;
    bottom: -3px;
    content: " ";
    height: 0.1rem;
    left: 20%;
    position: absolute;
    transition: width .5s;
    width: 0;
}


.payment-option__icon {
    height: 30px!important;
    max-width: 100%;
}

.payment-option__discount {
    font-size: 13px!important;
    text-transform: lowercase;
    color: #064121!important;
}

.badge-success {
	background-color: #2f8c40  !important;
