css:
.top-bar__countdown {
    font-size: 15px;
    padding: var(--spacer-2) var(--spacer-3);
}

.header {
    position: relative;
    padding-bottom: 15px;
    border-bottom: none;
    box-shadow: 0px 4px 6px #29292991;
    background: #2593A1;
}
.header__logo {
    height: auto;
    max-height: calc(var(--font-size-lg)*var(--line-height-lg) + var(--spacer-2)*2);
    max-width: 160px;
    width: auto;
}

.top-bar {
background: #E9E9E9;
font-size: 15px;
font-family: "Roboto",sans-serif;
}

.top-bar__page-links a {
    font-weight: 400;  
}

.top-bar__page-links a:hover {
    text-decoration: underline;
}

.top-bar__nav {
color: #3a3a3a;
background-color: #e9e9e9;
font-size: 15px;
}

.top-bar__nav {
    padding-left: var(--spacer-3);
    padding-right: var(--spacer-3);
    padding-top: var(--spacer-2);
    padding-bottom: var(--spacer-2);
}

.header__buttons .btn {
color: white;
}

.header__buttons .btn:hover {
color: white;
background: #0F5663;
}

.badge-primary {
    color: #ffffff;
    background-color: #f55835;
    border-radius: 20%;
}

.header__toggler-icon:hover {
  color: white;
  background: #0F5663;
}

img, svg {
    vertical-align: top !important;
}

.header__nav a {
    text-transform: none;
    color: #e4e4e4;
    font-size: 15px;
    font-weight: 400;
    text-shadow: 1px 1px #707070;
}

.header__nav a:hover {
background-color: #E6E6E6;
color: black;
   text-shadow:none;
}

.header__nav--full a {
    margin-top: var(--spacer-2);
    padding: 0 var(--spacer-3);
}

.header__search-input {
    padding: 0 var(--spacer-3);
    border-radius: 17px;
    font-size: 14px;
    line-height: var(--line-height-lg);
    height: calc(var(--line-height-lg)*var(--font-size-lg) + var(--spacer-2)*1);
}

a {
    color: #1d1d1d;
    text-decoration: none;
}

a:hover {
color: #1d1d1d;
text-decoration: none;
}

.prices--big {
font-size: 20px;
font-weight: 500;
}

.variations__option {
min-height: 2.3rem;
min-width: 2.3rem;
margin-bottom: var(--spacer-2);
border: none !important;
}

.btn-light {
color: #000000;
background-color: #f3f3f3;
border-color: #ffffff;
border-radius: 7px;
}

.btn-light:hover {
color: #000000;
background-color: #f3f3f3;
border-color: black;
border-radius: 7px;
}

.product__buy button {
text-transform: none;
transition: 0.5s;
background: #2593A1;
background-size: 200% auto;
color: white;            
box-shadow: none;
border-radius: 4px;
display: block;
border: none;
box-shadow: 1px 1px 2px darkgrey;
}

.product__buy button:hover {
background: #19646D;
color: white;
text-decoration: none;
box-shadow: 1px 1px 2px darkgrey;
}

.btn-primary {
color: #f3f3f3;
background: #2593A1;
border-color: gray;
border: none;
border-radius: 4px 4px 4px 4px;
box-shadow: 1px 1px 2px darkgrey;
}

.btn-primary:hover {
color: #f3f3f3;
background: #19646D;
border: none;
box-shadow: 1px 1px 2px darkgrey;
}

.btn-outline-secondary {
color: #000000;
    background-color: #f1f1f1;
    border-color: #f1f1f1;
    border-radius: 4px 4px 4px 4px;
}

.btn-outline-secondary:hover {
color: #000000;
    background-color: #f1f1f1;
    border-color: #f1f1f1;
    border-radius: 4px 4px 4px 4px;
}

.prices:not(.prices--big) {
    font-size: 20px;
    font-weight: 500;
    padding-bottom: 15px;
}

.mt-3, .my-3 {
    margin-top: 1rem;
    font-size: 14px;
    color: #ba0c2f;
}

.product__discount {
    color: #064121;
}

.lead {
    font-weight: 400;
}
.prices--literal small {
    font-size: 70%;
}

.footer {
background: #f1f1f1;
border-top: var(--spacer-1) solid #4f4f4f00;
font-size: 13px;
color: #666;
}

.mt-4, .my-4 {
    margin-top: 50px;
}

@media (max-width: 1200px)
.h1, h1 {
    font-size: 30px;
}

.h1, h1 {
    font-size: 30px;
}

.form-control {
    display: block;
    width: 100%;
    height: calc(1.5em + 0.75rem + 2px);
    padding: 0.375rem 0.75rem;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: #495057;
    background-color: #fff;
    background-clip: padding-box;
    border: 1.5px solid #bbbbbb;
    border-radius: 0.25rem;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
}

.product-card__offer-stamp {
    background-color: #d10014;
    color: #ffffff;
    min-width: 55px;
    font-size: 80%;
    font-weight: 400;
    line-height: 2.2;
    text-align: center;
    position: absolute;
    z-index: 9;
    top: unset;
    right: 0;
    border-radius: var(--border-radius);
    transition: border-radius .3s;
    opacity: 1;
}

.product-card__picture img {
    max-width: 88%;
}

.product__sticky {
    position: fixed;
    bottom: 0;
    left: 0;
    z-index: 500;
    width: 100%;
    padding: var(--spacer-2) var(--spacer-3);
    background: rgb(255 255 255 / 65%);
    box-shadow: var(--box-shadow-lg);
    backdrop-filter: saturate(180%) blur(20px);
}

.picture img {
    width: auto;
    max-width: 100%;
    height: auto;
    margin: 0 auto;
}

.h5, h5 {
    font-size: 15px;
}

.card {
    border: none;
    background: rgb(255 255 255 / 79%);
    backdrop-filter: saturate(180%) blur(20px);

}

.card-header {
padding: 0.75rem 1.25rem;
margin-bottom: 0;
background-color: #1F4F66;
color: whitesmoke;
border-bottom: none;
}

.close:hover {
    color: #fff;
    text-decoration: none;
}

.close {
    float: right;
    font-size: 1.5rem;
    font-weight: 700;
    line-height: 1;
    color: whitesmoke;
    text-shadow: none;
    opacity: 1;
}

.cart-item .close {
    padding-left: var(--spacer-2);
    color: #525252 !important;
    font-size: var(--font-size);
}

.card-footer {
    padding: 0.45rem 1.25rem;
    background-color: rgb(0 0 0 / 7%);
    border-top: none;
}

.search__header {
    position: relative;
    display: flex;
    align-items: center;
    background: #ededed;
}

.form-control {
    display: block;
    width: 100%;
    height: calc(1.5em + 0.75rem + 2px);
    padding: 0.375rem 0.75rem;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: #495057;
    background-color: #fff;
    background-clip: padding-box;
    border: 1.5px solid #ffffff;
    border-radius: 0.25rem;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
}

.prices--big .prices__value {
    font-size: 30PX;
    margin-bottom: var(--spacer-1);
    color: #064121;
    font-weight: 500;
}

.table {
    width: 100%;
    margin-bottom: 1rem;
    color: #212529;
    box-shadow: 0 2px 4px 2px rgb(0 0 0 / 16%);
}

.table td, .table th {
    padding: 0.75rem;
    vertical-align: top;
    border-top: none;
}

.product__sticky-buy .btn-primary{
background-color: #2593a1;
border-color: #2593a1;
}

.product__sticky-buy .btn-primary:hover{
background-color: #19646D;
border-color: #19646D;
}

.mt-3, .my-3 {
    margin-top: 1rem;
    font-size: 14px;
    color: #404040;
}

@media (min-width: 576px)
.header__logo {
    max-width: 180px !important;
    max-height: 60px;
}

.prices {
    line-height: unset;
    color: #5d5d5d;
}

.product__payment>a {
    display: flex;
    justify-content: space-between;
    align-items: center;
    background: #f7f7f7;
    color: #2e2e2e;
    margin-top: 15px;
}

.input-group:not(.has-validation)>.form-control:not(:last-child) {
    border-top-right-radius: 0;
    border-bottom-right-radius: 0;
    border-color: #e9e9e9;
}

.info-bar {
    width: 100%;
    max-width: 80rem;
    margin-left: auto;
    margin-right: auto;
    background: #333333;
    color: white;
}

.menu {
    display: none;
    width: 300px;
    position: fixed;
    top: 0;
    left: 0;
    z-index: 1100;
    height: 100vh;
    overflow-y: auto;
    flex-direction: column;
    justify-content: space-between;
    box-shadow: 0px 2px 4px black;
    background: rgb(255 255 255 / 65%);
    backdrop-filter: saturate(180%) blur(20px);
}

.menu__item {
    display: flex;
    align-items: center;
    color: black;
    padding: var(--spacer-3);
    border-bottom: var(--border-width) solid #aeb0b3;
    transition: background-color .15s;
}

.menu__item:hover {
color: black;
background: rgb(255 255 255 / 65%);
backdrop-filter: saturate(180%) blur(20px);
}

.menu__footer a {
    color: black;
    text-decoration: none;
}

.menu__footer a: hover {
    color: black;
    text-decoration: none;
}

.menu__link {
    text-align: center;
    display: block;
    border-top: var(--border-width) dashed var(--primary-lightest-yiq);
    margin: var(--spacer-3);
    padding: var(--spacer-3) var(--spacer-2);
    line-height: 1;
    color: #000000;
}

.menu__link:hover {
    text-decoration: none;
    color: #000000;
}

.gallery__thumbs {
    z-index: 2;
    position: absolute;
    width: auto;
    padding: 0;
    display: inline-flex;
    top: 100%;
    margin: 0px 0 0 !imoportant;
}

.product__picture picture.picture img {
    position: absolute;
    top: 0;
    left: 0;
    background: white;
}

.search__status .close {
    transition: opacity .2s;
    color: #7a7a7a;
}

.product-card__name {
--max-lines: 3;
padding-top: var(--spacer-1);
font-size: var(--font-size-sm);
line-height: var(--line-height-sm);
overflow: hidden;
z-index: 1;
color: #323232 !important;
font-weight: 500;
}

.card-header:first-child {
    border-radius: calc(0.25rem - 1px) calc(0.25rem - 1px) 0 0;
    background: #f1f1f1;
    color: #404040;
}

.product-quickview__box {
    width: 90vw;
    margin-left: 5vw;
    max-height: 90vh;
    margin-top: 5vh;
    border-radius: var(--border-radius);
    background: white;
}

button.close {
    padding: 0;
    background-color: transparent;
    border: 0;
    color: black;
}

.minicart__summary {
    color: #2b2b2b;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding-bottom: var(--spacer-3);
}
.minicart__subtotal {
    text-align: right;
    color: #1c1c1c;
}

@media (min-width: 576px)
.app__header {
    padding: var(--spacer-1);
    text-align: right;
    font-size: var(--font-size);
    color: whitesmoke;
}

.app__header {
    background: #1f4f66;
    padding: var(--spacer-1);
    box-shadow: var(--box-shadow-sm);
    text-align: center;
    font-size: var(--font-size-sm);
    box-shadow: 0px 2px 4px #000000a1;
}

.app__contact span:not(:last-child) {
    margin-right: var(--spacer-3);
    color: whitesmoke;
}

.app__contact span:not(:first-child) {
    color: whitesmoke;
}

.app__safe {
    display: inline;
}

.header__toggler-icon {
    color: #ffffff;
    font-size: 1.3rem;
    margin-right: var(--spacer-1);
    margin-bottom: -0.13rem;
}

.breadcrumb {
    background-color: transparent;
    border-radius: 0.25rem;
    display: flex;
    flex-wrap: wrap;
    list-style: none;
    margin-bottom: 1rem;
    padding: 0.5rem 0;
    font-size: 13px;
}

.header__nav {
    font-size: var(--font-size-sm);
    line-height: var(--line-height-lg);
    text-align: center;
    padding-top: 7px;
}
